// ============================================
// MERGE DATA FROM ALL 4 SUPABASE QUERIES
// ============================================

// Get data from each Supabase node by name
const restaurants   = $('Supabase - Get Restaurants').all().map(i => i.json);
const allScores     = $('Supabase - Get Occasion Scores').all().map(i => i.json);
const allTags       = $('Supabase - Get Tags').all().map(i => i.json);
const neighborhoods = $('Supabase - Get Neighborhoods').all().map(i => i.json);

// Get user inputs
const userNeighborhood = $('Parse User Input').item.json.neighborhood;
const occasion         = $('Parse User Input').item.json.occasion;
const specialRequest   = $('Parse User Input').item.json.special_request;
const priceLevel       = $('Parse User Input').item.json.price_level;

// ---- BUILD LOOKUP MAPS ----

// Neighborhood ID -> name
const neighborhoodMap = {};
neighborhoods.forEach(n => {
  neighborhoodMap[n.id] = n.name;
});

// Restaurant ID -> scores
const scoresMap = {};
allScores.forEach(s => {
  scoresMap[s.restaurant_id] = s;
});

// Restaurant ID -> tags array
const tagsMap = {};
allTags.forEach(t => {
  if (!tagsMap[t.restaurant_id]) tagsMap[t.restaurant_id] = [];
  tagsMap[t.restaurant_id].push(t.tag_text);
});

// ---- MERGE INTO FULL PROFILES ----
let profiles = restaurants.map(r => {
  const neighborhoodName = neighborhoodMap[r.neighborhood_id] || 'Unknown';
  const scores = scoresMap[r.id] || {};
  const tags = tagsMap[r.id] || [];

  return {
    ...r,
    neighborhood_name: neighborhoodName,
    date_friendly_score: scores.date_friendly_score || null,
    group_friendly_score: scores.group_friendly_score || null,
    family_friendly_score: scores.family_friendly_score || null,
    romantic_rating: scores.romantic_rating || null,
    business_lunch_score: scores.business_lunch_score || null,
    solo_dining_score: scores.solo_dining_score || null,
    hole_in_wall_factor: scores.hole_in_wall_factor || null,
    tags: tags
  };
});

// ---- FILTER BY NEIGHBORHOOD (if not "Anywhere") ----
if (userNeighborhood && userNeighborhood !== 'Anywhere') {
  profiles = profiles.filter(p =>
    p.neighborhood_name.toLowerCase() === userNeighborhood.toLowerCase()
  );
}

// ---- FILTER: Only restaurants with review summaries ----
profiles = profiles.filter(p => p.google_review_summary != null);

// ---- HANDLE NO RESULTS ----
if (profiles.length === 0) {
  return [{
    json: {
      no_results: true,
      prompt: '',
      top_restaurants: [],
      occasion: occasion
    }
  }];
}

// ---- MAP OCCASION TO SCORE FIELD ----
const occasionScoreMap = {
  'Date Night': 'date_friendly_score',
  'Group Hangout': 'group_friendly_score',
  'Family Dinner': 'family_friendly_score',
  'Business Lunch': 'business_lunch_score',
  'Solo Dining': 'solo_dining_score',
  'Special Occasion': 'romantic_rating'
};
const scoreField = occasionScoreMap[occasion] || 'date_friendly_score';

// ---- SORT BY OCCASION SCORE (descending) ----
profiles.sort((a, b) => {
  const scoreA = parseInt(a[scoreField]) || 0;
  const scoreB = parseInt(b[scoreField]) || 0;
  if (scoreB !== scoreA) return scoreB - scoreA;
  // Tiebreak by google_rating
  return (parseFloat(b.google_rating) || 0) - (parseFloat(a.google_rating) || 0);
});

// Take top 10
const top10 = profiles.slice(0, 10);

// ---- BUILD GEMINI PROMPT ----
const restaurantList = top10.map((d, i) => {
  const summary = typeof d.google_review_summary === 'object'
    ? JSON.stringify(d.google_review_summary)
    : (d.google_review_summary || 'N/A');

  return `${i+1}. ${d.name}\n   Address: ${d.address}\n   Neighborhood: ${d.neighborhood_name}\n   Price: ${d.price_level}\n   Google Rating: ${d.google_rating}/5 (${d.google_review_count} reviews)\n   ${occasion} Score: ${d[scoreField]}/10\n   Atmosphere: ${d.noise_level || 'N/A'}, ${d.lighting_ambiance || 'N/A'}\n   Review Summary: ${summary}\n   Editor's Note: ${d.editors_note || 'N/A'}\n   Best For: ${d.best_for_oneliner || 'N/A'}\n   Tags: ${d.tags.length > 0 ? d.tags.join(', ') : 'N/A'}`;
}).join('\n\n---\n\n');

const prompt = `You are Donde, a warm and knowledgeable Chicago restaurant concierge. A user is looking for the perfect dining spot.

USER REQUEST:
- Occasion: ${occasion}
- Budget: ${priceLevel}
- Neighborhood: ${userNeighborhood}
- Special Request: ${specialRequest || 'None'}

TOP 10 CANDIDATES (ranked by ${occasion} score):

${restaurantList}

YOUR TASK:
Pick THE ONE BEST restaurant for this person. Consider their occasion, special request, and overall fit.

Respond ONLY in this exact JSON format (no markdown, no backticks):
{
  "restaurant_index": 0,
  "recommendation": "A warm, personal 80-120 word paragraph explaining WHY this restaurant is the perfect match for their request. Mention specific things about the food, atmosphere, and what makes it special for their occasion.",
  "insider_tip": "One specific, actionable insider tip (e.g., ask for the corner booth, try the off-menu horchata, go on Tuesday for half-price bottles)"
}`;

return [{
  json: {
    no_results: false,
    prompt: prompt,
    top_restaurants: top10,
    occasion: occasion,
    scoreField: scoreField
  }
}];

// ============================================
// BUILD THE RICH JSON RESPONSE FOR THE UI
// ============================================

const geminiResponse = $input.item.json;
const prevData = $('Merge Data + Rank + Build Prompt').item.json;
const topRestaurants = prevData.top_restaurants;

try {
  // Parse Gemini response
  const responseText = geminiResponse.candidates[0].content.parts[0].text;
  let parsed;

  try {
    parsed = JSON.parse(responseText);
  } catch (e) {
    // Strip markdown backticks if Gemini wrapped the JSON
    const cleaned = responseText.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    parsed = JSON.parse(cleaned);
  }

  const idx = parseInt(parsed.restaurant_index) || 0;
  const chosen = topRestaurants[idx] || topRestaurants[0];

  return [{
    json: {
      success: true,
      recommendation: parsed.recommendation || 'A great choice for your occasion!',
      insider_tip: parsed.insider_tip || null,
      restaurant: {
        id: chosen.id,
        name: chosen.name,
        address: chosen.address,
        google_place_id: chosen.google_place_id,
        google_rating: chosen.google_rating,
        google_review_count: chosen.google_review_count,
        price_level: chosen.price_level,
        phone: chosen.phone,
        website: chosen.website,
        noise_level: chosen.noise_level,
        lighting_ambiance: chosen.lighting_ambiance,
        dress_code: chosen.dress_code,
        outdoor_seating: chosen.outdoor_seating,
        live_music: chosen.live_music,
        pet_friendly: chosen.pet_friendly,
        parking_availability: chosen.parking_availability,
        sentiment_breakdown: chosen.sentiment_breakdown,
        sentiment_score: chosen.sentiment_score,
        editors_note: chosen.editors_note,
        best_for_oneliner: chosen.best_for_oneliner,
        neighborhood_name: chosen.neighborhood_name
      },
      scores: {
        date_friendly_score: chosen.date_friendly_score,
        group_friendly_score: chosen.group_friendly_score,
        family_friendly_score: chosen.family_friendly_score,
        romantic_rating: chosen.romantic_rating,
        business_lunch_score: chosen.business_lunch_score,
        solo_dining_score: chosen.solo_dining_score,
        hole_in_wall_factor: chosen.hole_in_wall_factor
      },
      tags: Array.isArray(chosen.tags) ? chosen.tags.filter(t => t && t !== 'null') : [],
      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  // Fallback: return the top-ranked restaurant without Gemini enrichment
  const fallback = topRestaurants[0];
  return [{
    json: {
      success: true,
      recommendation: fallback.editors_note || 'A top pick for your occasion based on our scores!',
      insider_tip: null,
      restaurant: {
        id: fallback.id,
        name: fallback.name,
        address: fallback.address,
        google_place_id: fallback.google_place_id,
        google_rating: fallback.google_rating,
        google_review_count: fallback.google_review_count,
        price_level: fallback.price_level,
        phone: fallback.phone,
        website: fallback.website,
        noise_level: fallback.noise_level,
        lighting_ambiance: fallback.lighting_ambiance,
        dress_code: fallback.dress_code,
        outdoor_seating: fallback.outdoor_seating,
        live_music: fallback.live_music,
        pet_friendly: fallback.pet_friendly,
        parking_availability: fallback.parking_availability,
        sentiment_breakdown: fallback.sentiment_breakdown,
        sentiment_score: fallback.sentiment_score,
        editors_note: fallback.editors_note,
        best_for_oneliner: fallback.best_for_oneliner,
        neighborhood_name: fallback.neighborhood_name
      },
      scores: {
        date_friendly_score: fallback.date_friendly_score,
        group_friendly_score: fallback.group_friendly_score,
        family_friendly_score: fallback.family_friendly_score,
        romantic_rating: fallback.romantic_rating,
        business_lunch_score: fallback.business_lunch_score,
        solo_dining_score: fallback.solo_dining_score,
        hole_in_wall_factor: fallback.hole_in_wall_factor
      },
      tags: Array.isArray(fallback.tags) ? fallback.tags.filter(t => t && t !== 'null') : [],
      timestamp: new Date().toISOString()
    }
  }];
}

